// src/App.tsx
import React, { useState, useEffect } from "react";
import {
  createUserWithEmailAndPassword,
  signInWithEmailAndPassword,
  signOut,
  onAuthStateChanged,
  User
} from "firebase/auth";
import { auth } from "./firebase";

function App() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState<User | null>(null);
  const [message, setMessage] = useState("");

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe(); // cleanup listener
  }, []);

  const handleRegister = async () => {
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      setMessage("Account created!");
    } catch (error: any) {
      setMessage(error.message);
    }
  };

  const handleLogin = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      setMessage("Logged in!");
    } catch (error: any) {
      setMessage(error.message);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setMessage("Logged out.");
    } catch (error: any) {
      setMessage(error.message);
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "2rem auto", padding: "1rem", textAlign: "center", border: "1px solid #ccc", borderRadius: "8px" }}>
      <h2>Firebase Auth</h2>

      <input
        type="email"
        placeholder="Email"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        style={{ display: "block", margin: "10px auto", width: "90%" }}
      />

      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        style={{ display: "block", margin: "10px auto", width: "90%" }}
      />

      <div style={{ margin: "10px 0" }}>
        <button onClick={handleRegister} style={{ marginRight: "10px" }}>Register</button>
        <button onClick={handleLogin} style={{ marginRight: "10px" }}>Login</button>
        <button onClick={handleLogout}>Logout</button>
      </div>

      <p style={{ color: "green" }}>{message}</p>

      {user ? (
        <p>Logged in as: {user.email}</p>
      ) : (
        <p>No user logged in.</p>
      )}
    </div>
  );
}

export default App;
